// The second module should accept a number and convert it to a USD string value.
//
// HINT: You will want to Google examples of this.
// I found a couple reasonable examples of this in a simple 5-minute search.

function convertNumber(number) {
    number = number.toString();
    dollars = number.split('.')[0];
    cents = (number.split('.')[1] || '') +'00';
    dollars = dollars.split('').reverse().join('')
        .replace(/(\d{3}(?!$))/g, '$1,')
        .split('').reverse().join('');
    return '$' + dollars + '.' + cents.slice(0, 2);
}

module.exports = convertNumber;
